function addFields(){var e=document.createElement("div"),n=document.getElementById("prof_form_group");n.getElementsByTagName("input").length;e.innerHTML='<label>Field name:</label><input type="text" class="form-control" id="p_name" name="field_name[]" >',e.innerHTML+='<label>Field type</label> <br><select class="form-control" id="p_name" name="field_type[]">  <option value="text">Text</option>  <option value="number">Number</option></select>',n.appendChild(e)}